import React, { useState } from 'react'
import { useHistory } from 'react-router-dom';
import './event.css'
import { FaTimesCircle } from 'react-icons/fa'
import './reminder'

const Event = () => {
  const [isEventOpen, setIsEventOpen] = useState(false)

    const history = useHistory();
    const navigateTo = () => history.push('/reminder');//eg.history.push('/login');
  

  return (
    <div>
      <button id='add-event' onClick={() => setIsEventOpen(true)}>
        Add Event
      </button>
      {isEventOpen && <div className='overlay'></div>}
      {isEventOpen && (
        <div className='event-form'>
          <header>
            <h2>Add New Event</h2>
            <FaTimesCircle
              className='faTimes'
              onClick={() => setIsEventOpen(false)}
            />
          </header>
        

          <h6 id='event'>Event</h6>
          <button id='reminder' type='button' onClick={navigateTo}>
            Reminder
          </button>

         
          
        </div>
      )}

      
    </div>
  )
}

export default Event
