import React, { useState } from 'react'
import './event.css'
import Reminder  from './Reminder'
import { FaTimesCircle } from 'react-icons/fa'
import { useHistory  } from 'react-router-dom'

const Event = () => {
  const [isEventOpen, setIsEventOpen] = useState(false)

function RenderReminderSettingPage() {
  let history = useHistory()
}
  

  return (
    <div>
      <button id='add-event' onClick={() => setIsEventOpen(true)}>
        Add Event
      </button>
      {isEventOpen && <div className='overlay'></div>}
      {isEventOpen && (
        <div className='event-form'>
          <header>
            <h2>Add New Event</h2>
            <FaTimesCircle
              className='faTimes'
              onClick={() => setIsEventOpen(false)}
            />
          </header>
        

          <h6 id='event'>Event</h6>
          
              <button id='reminder' onClick = {() => {history.pushState('/reminder')}}>Reminder</button>
            
          
          
  
                </div>
              )}

      
    </div>
  )
}

export default Event
